import React, { useState } from "react";
import "./App.css";

function App() {
  const [board, setBoard] = useState(Array(9).fill(null));
  const [isXNext, setIsXNext] = useState(true);
  const [moves, setMoves] = useState([]); 
  const MOVE_LIMIT = 5; 

  const handleClick = (index) => {
    if (board[index] || checkWinner()) return;

    const newBoard = [...board];
    newBoard[index] = isXNext ? "X" : "O";

    const newMoves = [...moves, index]; // Add new move

    if (newMoves.length > MOVE_LIMIT) {
      const oldestMove = newMoves.shift(); // Remove oldest move
      newBoard[oldestMove] = null; // Clear the old position
    }

    setBoard(newBoard);
    setMoves(newMoves);
    setIsXNext(!isXNext);
  };

  const checkWinner = () => {
    const winPatterns = [
      [0, 1, 2], [3, 4, 5], [6, 7, 8],
      [0, 3, 6], [1, 4, 7], [2, 5, 8],
      [0, 4, 8], [2, 4, 6]
    ];
    for (let pattern of winPatterns) {
      const [a, b, c] = pattern;
      if (board[a] && board[a] === board[b] && board[a] === board[c]) {
        return board[a];
      }
    }
    return null;
  };

  const resetGame = () => {
    setBoard(Array(9).fill(null));
    setMoves([]);
    setIsXNext(true);
  };

  return (
    <div className="game">
      <h1>Tic-Tac-Toe (Hard Mode)</h1>
      <div className="board">
        {board.map((cell, index) => (
          <button 
            key={index} 
            className="cell" 
            onClick={() => handleClick(index)}
            style={{ backgroundColor: cell === "X" ? "yellow" : cell === "O" ? "red" : "white" }}
          >
            {cell}
          </button>
        ))}
      </div>
      <p>{checkWinner() ? `Winner: ${checkWinner()}` : `Next Player: ${isXNext ? "X" : "O"}`}</p>
      <button onClick={resetGame} className="reset">Reset</button>
    </div>
  );
}

export default App;
